package com.naver.mage4j.external.varien;

public class Varien_Exception extends RuntimeException {

	public Varien_Exception() {
		super();
	}

	public Varien_Exception(String message, Throwable cause) {
		super(message, cause);
	}

	public Varien_Exception(String message) {
		super(message);
	}

	public Varien_Exception(Throwable cause) {
		super(cause);
	}

}
