package com.naver.mage4j.external.zend.controller;

import com.naver.mage4j.external.zend.Zend_Exception;

public class Zend_Controller_Exception extends Zend_Exception {

	public Zend_Controller_Exception() {
		super();
	}

	public Zend_Controller_Exception(String message, Throwable cause) {
		super(message, cause);
	}

	public Zend_Controller_Exception(String message) {
		super(message);
	}

	public Zend_Controller_Exception(Throwable cause) {
		super(cause);
	}

}
