package com.naver.mage4j.external.zend.controller.response;

import com.naver.mage4j.external.zend.controller.Zend_Controller_Exception;

public class Zend_Controller_Response_Exception extends Zend_Controller_Exception {

	public Zend_Controller_Response_Exception() {
		super();
	}

	public Zend_Controller_Response_Exception(String message, Throwable cause) {
		super(message, cause);
	}

	public Zend_Controller_Response_Exception(String message) {
		super(message);
	}

	public Zend_Controller_Response_Exception(Throwable cause) {
		super(cause);
	}

}
